$(document).ready(function () {
  var swiper = new Swiper(".swiper-container.slide-1 .mySwiper", {
    spaceBetween: 10,
    slidesPerView: 5.5,
    freeMode: true,
    watchSlidesProgress: true,
    breakpoints: {
      576: {
        slidesPerView: 3.5,
      },
    },
  });
  var swiper2 = new Swiper(".swiper-container.slide-1 .mySwiper2", {
    spaceBetween: 10,
    navigation: {
      nextEl: ".swiper-container.slide-1 .swiper-button-next",
      prevEl: ".swiper-container.slide-1 .swiper-button-prev",
    },
    thumbs: {
      swiper: swiper,
    },
  });

  var swiper3 = new Swiper(".swiper-container.slide-2 .mySwiper", {
    spaceBetween: 10,
    slidesPerView: 5.5,
    freeMode: true,
    watchSlidesProgress: true,
    breakpoints: {
      576: {
        slidesPerView: 3.5,
      },
    },
  });
  var swiper4 = new Swiper(".swiper-container.slide-2 .mySwiper2", {
    spaceBetween: 10,
    navigation: {
      nextEl: ".swiper-container.slide-2 .swiper-button-next",
      prevEl: ".swiper-container.slide-2 .swiper-button-prev",
    },
    thumbs: {
      swiper: swiper3,
    },
  });

  const $lgContainer = document.getElementById("gallery-container");

  const lg = lightGallery($lgContainer, {
    speed: 500,
    showZoomInOutIcons: true,
    actualSize: false,
    // plugins: [lgZoom],
    download: false,
    counter: false,
  });

  btnTest = document.querySelector(".img-zoom");
  zoomItem = document.querySelector(".my-zoom-item");
  btnTest.addEventListener("click", () => {
    zoomItem.click();
  });
});

